  geom_errorbar(data=ag.ci.df, mapping=aes(x=perc, ymin=bs.low, ymax=bs.high), width=0.01, size=0.1, color="red")+
  labs(title="Growth pattern for Stk11 KPTC tumours", y="Normalised growth rate", x="Percentile")+
  geom_vline(xintercept=c(0.95), linetype="dotted")+
  geom_hline(yintercept=1.0, linetype="dotted")+
  scale_y_continuous(limits=c(0.8,1.4))+
  theme_classic()
a
ggsave("Desktop/new/plots/Stk11.kptc.pdf", width=5, height=10)

